using LeetCode;
using System;
using System.Collections.Generic;
using Xunit;

namespace LeetCodeTest
{
    public class LongestPalindromicSubstringTest
    {
        [Theory]
        [InlineData("babad", "bab")]
        [InlineData("cbbd", "bb")]
        [InlineData("a", "a")]
        [InlineData("ac", "a")]
        [InlineData("aacabdkacaa", "aca")]
        [InlineData("", "wyyw")]
        [InlineData("", "khvhk")]
        [InlineData("", "ljpjl")]
        public void Test1(string input, string expected)
        {
            var container = new LongestPalindromicSubstring();
            var result = container.LongestPalindrome(input);

            Assert.Equal(expected, result);
        }
    }
}